mapper.select:
SELECT * 
FROM (SELECT rownum rn, a.* 
     FROM (SELECT *
           FROM ${tableName}
           ORDER BY qna_no DESC
          ) a
      )
 WHERE rn BETWEEN #{start} AND #{end}

mapper.update:
UPDATE qnaboard_9999
SET title = #{title}, content = #{content}, updatedate = sysdate
WHERE qna_no = #{qna_no}
